### Store the path of all the images in a folder to an array
images_path = []

def get_images_path():
    global images_path

    path = '/home/sam/dev/ai-internship/cell_images'

    for files in os.listdir(path):
        images_path.append(files)

    for c, t in enumerate(images_path):
        images_path[c] = t[:-4]
        images_path[c] = int(images_path[c])

    images_path.sort()

    for c, t in enumerate(images_path):
        images_path[c] = 'cell_images/' + str(t) + '.png'

    return images_path

### Store all the images into a PIL image format
images = []

def get_images():
    global images

    for image in range(len(images_path)):
        images.append(np.array(Image.open(images_path[image])))

    return images


### Lambda functions for preprocessing
sobel = lambda a : skimage.filters.sobel(a)
hyst = lambda a, b, c : skimage.filters.apply_hysteresis_threshold(a, b, c)
thresh = lambda a : skimage.filters.threshold_otsu(a)
otsu = lambda a, thresh : a > thresh
labels = lambda a : skimage.morphology.label(a)
labeled_array = lambda a : scipy.ndimage.label(a)
num_features = lambda a : scipy.ndimage.label(a)[-1]


def preprocessing(image, low, high):
    image = labels(hyst(image, low, high))

    return image


