### Store all the images that can be counted with image processing
labeled_images = []
indices = []

def get_labeled_images():
    global labeled_images, indices

    num_labeled_images = 0

    for i in range(len(images)):
        temp_image = preprocessing(images[i], 40, 150)
        if num_features(temp_image) == num_cells[i]:
            num_labeled_images += 1
            #print(i)
            labeled_images.append(temp_image)
            indices.append(i)
    #print("Labeled images: " + str(num_labeled_images))

    return labeled_images


### Function to show the images with bounding boxes
def show(imgs):
    if not isinstance(imgs, list):
        imgs = [imgs]
    fix, axs = plt.subplots(ncols=len(imgs), squeeze=False)
    for i, img in enumerate(imgs):
        img = img.detach()
        img = F.to_pil_image(img)
        axs[0, i].imshow(np.asarray(img))
        axs[0, i].set(xticklabels=[], yticklabels=[], xticks=[], yticks=[])


### Make everything that isn't black white
def black_white():
    for image in range(len(labeled_images)):
        threshold = 0.1
        labeled_images[image] = labeled_images[image] < threshold

    return labeled_images

boxes_list = []
c = 0

def get_boxes_info():
    global boxes_list, c

    for image in labeled_images:
        test = image
        #print(test, type(test))
        test = torch.from_numpy(test)

        obj_ids = torch.unique(test)
        obj_ids = obj_ids[1:]
        test = test == obj_ids[:, None, None]

        boxes = masks_to_boxes(test)
        boxes_list.append(boxes)

        c += 1

    return boxes_list

#print(len(boxes_list))

normalized_boxes = []

### Normalize boxes information to one
def normalize_boxes(boxes):
    global normalized_boxes

    for box in range(len(boxes)):
        normalized_boxes.append(normalize(boxes[box], p=1.0, dim=1))
    #    boxes_list[box] = normalize(boxes_list[box], p=1.0, dim=1)

    return boxes_list

### Write box information to txt files
def write_box_info_to_file(boxes):
    n = 0

    for c, i in enumerate(boxes):
        for c2, j in enumerate(i):
            for k in j:
                w = str(k.item()) + " "
                filename = "data/labels/" + str(c) + ".txt"
                f = open(filename, "a")
                if n % 4 == 0 and n != 0:
                    f.write("\n")
                    f.write("0 ")

                f.write(w)
                n += 1
    print("UPDATE")


### Write images to a folder
def write_images_to_folder():
    for c, image in enumerate(labeled_images):
        imageio.imwrite("temp/" + str(c) + ".png", image.astype(int))
        #print(type(Image.fromarray(image)))
        #image = Image.fromarray(image)
        #image.save("temp/" + str(c) + ".png")

def show_drawn_boxes(image_number):
    test = preprocessing(images[image_number], 40, 150)
    test = torch.from_numpy(test)

    t_image = read_image("data/images/train/{image_number}.png".format(image_number=image_number))

    obj_ids = torch.unique(test)
    obj_ids = obj_ids[1:]
    test = test == obj_ids[:, None, None]

    boxes = masks_to_boxes(test)
    drawn_boxes = draw_bounding_boxes(t_image, boxes, colors="red")

    show(drawn_boxes)

def main():
    get_images_path()
    get_images()
    get_labeled_images()
    #black_white()
    get_boxes_info()
    normalize_boxes(boxes_list)
    write_box_info_to_file(boxes_list)
    write_images_to_folder()

#main()

if __name__ == "__main__":
    main()
    print("SUCCESSFUL")
